import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { fetchProtectedData } from "../services/api";

const Dashboard = () => {
  const { logout, token } = useContext(AuthContext);
  const [data, setData] = useState(null);
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");
  const [selectedPanel, setSelectedPanel] = useState("inicio");

  useEffect(() => {
    const loadData = async () => {
      try {
        const response = await fetchProtectedData(token);
        setData(response);
        setUser(response.user);
      } catch (err) {
        setError("No autorizado o error al cargar datos");
      }
    };
    if (token) loadData();
  }, [token]);

  const renderPanel = () => {
    switch (selectedPanel) {
      case "inicio":
        return (
          <div>
            <h2>Bienvenido, {user.name}</h2>
            <p>Email: {user.email}</p>
          </div>
        );
      case "notificar":
        return (
          <div>
            <h2>Notificar producto dañado</h2>
            <p>Funcionalidad para subir fotos y reportar detalles...</p>
            {/* Aquí se agregará el formulario de carga más adelante */}
          </div>
        );
      case "inventario":
        return (
          <div>
            <h2>Inventario</h2>
            <p>Aquí va la lista de productos y stock...</p>
          </div>
        );
      default:
        return null;
    }
  };

  if (error) return <p>{error}</p>;
  if (!data || !user) return <p>Cargando...</p>;

  return (
    <div className="employee-dashboard">
      <aside className="sidebar">
        <h3>Menú</h3>
        <ul>
          <li onClick={() => setSelectedPanel("inicio")}>Inicio</li>
          <li onClick={() => setSelectedPanel("notificar")}>Notificar daño</li>
          <li onClick={() => setSelectedPanel("inventario")}>Inventario</li>
          <li onClick={logout}>Cerrar sesión</li>
        </ul>
      </aside>

      <main className="main-panel">
        {renderPanel()}
      </main>
    </div>
  );
};

export default Dashboard;
