import React, { useState } from 'react';
import { requestOtp, verifyOtp } from '../services/api';

export default function RecuperarContrasena() {
  const [usuario, setUsuario] = useState('');
  const [otp, setOtp] = useState('');
  const [nuevaPassword, setNuevaPassword] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [otpSolicitado, setOtpSolicitado] = useState(false);

  const solicitarOtp = async () => {
    try {
      const data = await requestOtp(usuario);
      setMensaje(data.mensaje);
      setOtpSolicitado(true);
    } catch (error) {
      setMensaje(error.message);
    }
  };

  const verificar = async () => {
    try {
      const data = await verifyOtp({ usuario, otp, nuevaPassword });
      setMensaje(data.mensaje);
      if (data.valido) {
        setUsuario('');
        setOtp('');
        setNuevaPassword('');
        setOtpSolicitado(false);
      }
    } catch (error) {
      setMensaje(error.message);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto' }}>
      <h2>Recuperar contraseña</h2>

      {!otpSolicitado ? (
        <>
          <input
            type="text"
            placeholder="Usuario"
            value={usuario}
            onChange={e => setUsuario(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <button onClick={solicitarOtp} disabled={!usuario}>
            Solicitar OTP
          </button>
        </>
      ) : (
        <>
          <input
            type="text"
            placeholder="OTP recibido"
            value={otp}
            onChange={e => setOtp(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <input
            type="password"
            placeholder="Nueva contraseña"
            value={nuevaPassword}
            onChange={e => setNuevaPassword(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <button onClick={verificar} disabled={!otp || !nuevaPassword}>
            Verificar y cambiar contraseña
          </button>
        </>
      )}

      <p>{mensaje}</p>
    </div>
  );
}
