import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { getUsers } from "../services/api";

const Dashboard = () => {
  const { logout, token } = useContext(AuthContext);
  const [users, setUsers] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const loadUsers = async () => {
      try {
        const response = await getUsers(token); // response es array de usuarios
        setUsers(response);
      } catch (err) {
        setError("No autorizado o error al cargar datos");
      }
    };

    if (token) loadUsers();
  }, [token]);

  if (error) return <p>{error}</p>;
  if (!users) return <p>Cargando usuarios...</p>;

  return (
    <div className="dashboard-container">
      <h1>Bienvenido al Admin</h1>
      <p className="intro-text">Lista de usuarios:</p>

      <div className="info-panel">
        {users.length === 0 ? (
          <p>No hay usuarios disponibles</p>
        ) : (
          <ul>
            {users.map((u, idx) => (
              <li key={idx}>
                Usuario: {u.usuario} - Rango: {u._userInfo?.rango || "Desconocido"}
                {/* Agrega más campos si quieres */}
              </li>
            ))}
          </ul>
        )}

        <button onClick={logout} className="logout-btn">
          Cerrar sesión
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
