// src/components/RecuperarContrasena.jsx
import React, { useState } from 'react';

export default function RecuperarContrasena() {
  const [usuario, setUsuario] = useState('');
  const [otp, setOtp] = useState('');
  const [nuevaPassword, setNuevaPassword] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [otpSolicitado, setOtpSolicitado] = useState(false);

  const solicitarOtp = async () => {
    const res = await fetch('/api/request-otp', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ usuario }),
    });
    const data = await res.json();
    setMensaje(data.mensaje || 'Error');
    if (res.ok) setOtpSolicitado(true);
  };

  const verificarOtp = async () => {
    const res = await fetch('/api/verify-otp', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ usuario, otp, nuevaPassword }),
    });
    const data = await res.json();
    setMensaje(data.mensaje || 'Error');
    if (data.valido) {
      setOtpSolicitado(false);
      setUsuario('');
      setOtp('');
      setNuevaPassword('');
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto' }}>
      <h2>Recuperar contraseña</h2>

      {!otpSolicitado && (
        <>
          <input
            type="text"
            placeholder="Usuario"
            value={usuario}
            onChange={e => setUsuario(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <button onClick={solicitarOtp} disabled={!usuario}>
            Solicitar OTP
          </button>
        </>
      )}

      {otpSolicitado && (
        <>
          <input
            type="text"
            placeholder="OTP recibido"
            value={otp}
            onChange={e => setOtp(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <input
            type="password"
            placeholder="Nueva contraseña"
            value={nuevaPassword}
            onChange={e => setNuevaPassword(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <button onClick={verificarOtp} disabled={!otp || !nuevaPassword}>
            Verificar OTP y cambiar contraseña
          </button>
        </>
      )}

      <p>{mensaje}</p>
    </div>
  );
}
