import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { fetchProtectedData, getUserById, updateUser } from "../services/api";

const Dashboard = () => {
  const { logout, token } = useContext(AuthContext);
  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [user, setUser] = useState(null);

  useEffect(() => {
    const loadUserData = async () => {
      try {
        const userId = JSON.parse(atob(token.split('.')[1])).id;
        const userData = await getUserById(userId, token);
        setUser(userData);
      } catch (err) {
        setError("No autorizado o error al cargar datos del usuario");
      }
    };

    if (token) loadUserData();
  }, [token]);

  useEffect(() => {
    const updateUserData = async () => {
      if (user) {
        try {
          const updatedUser = await updateUser(user.id, user, token);
          setUser(updatedUser);
        } catch (err) {
          setError("Error al actualizar datos del usuario");
        }
      }
    };

    updateUserData();
  }, [user, token]);

  useEffect(() => {
    const loadData = async () => {
      try {
        const response = await fetchProtectedData(token);
        setData(response);
      } catch (err) {
        setError("No autorizado o error al cargar datos");
      }
    };

    if (token) loadData();
  }, [token]);

  if (error) return <p>{error}</p>;
  if (!data) return <p>Cargando...</p>;

  return (
    <div className="dashboard-container">
      <h1>Bienvenido al Dashboard</h1>
      <p className="intro-text">Información</p>

      <div className="info-panel">
        <h2>Datos del usuario</h2>
        <p><strong>ID:</strong> {user ? user.id : "Cargando..."}</p>
        <p><strong>Nombre:</strong> {user ? user.name : "Cargando..."}</p>
        <p>Cambiar nombre de usuario</p>
        <input
          type="text"
          value={user ? user.name : ""}
          onChange={(e) => setUser({ ...user, name: e.target.value })}
          placeholder="Nuevo nombre de usuario"
        />
      </div> 

      <button
        onClick={async () => {
          try {
            const updatedUser = await updateUser(user.id, user, token);
            setUser(updatedUser);
          } catch (err) {
            setError("Error al actualizar datos del usuario");
          }
        }}
        className="update-btn"
      >
        Actualizar
      </button>

      <button onClick={logout} className="logout-btn">
        Cerrar sesión
      </button>
    </div>
  );
};

export default Dashboard;
