import React, { createContext, useState, useEffect } from 'react';
import { fetchProtectedData } from '../services/api';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(() => localStorage.getItem('token'));
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  const login = (jwt) => {
    localStorage.setItem('token', jwt);
    setToken(jwt);
  };

  const logout = () => {
    localStorage.removeItem('token');
    setToken(null);
    setUserData(null);
  };

  useEffect(() => {
    if (token) {
      fetchProtectedData(token)
        .then(data => {
          setUserData(data); // guardas info del usuario o permisos
          setLoading(false);
        })
        .catch(() => {
          logout(); // si el token es inválido, limpiamos todo
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  }, [token]);

  return (
    <AuthContext.Provider value={{ token, userData, login, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};
