import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { fetchProtectedData, getUsers } from "../services/api";

const DashboardAdmin = () => {
  const { logout, token } = useContext(AuthContext);

  const [protectedData, setProtectedData] = useState(null);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const loadData = async () => {
      try {
        // Carga datos generales protegidos
        const data = await fetchProtectedData(token);
        setProtectedData(data);

        // Carga lista de usuarios
        const usersList = await getUsers(token);
        setUsers(usersList);
      } catch (err) {
        setError("Error al cargar datos o usuarios: " + err.message);
      }
    };

    if (token) loadData();
  }, [token]);

  if (error) return <p>{error}</p>;
  if (!protectedData || users.length === 0) return <p>Cargando...</p>;

  return (
    <div className="dashboard-container">
      <h1>Dashboard Admin</h1>

      <section>
        <h2>Datos generales</h2>
      </section>

      <section>
        <h2>Usuarios</h2>
        <ul>
          {users.map((user) => (
            <li key={user.usuario || user.id}>
              {user.usuario} - {user._userInfo?.rango || "Sin rango"}
            </li>
          ))}
        </ul>
      </section>

      <button onClick={logout}>Cerrar sesi√≥n</button>
    </div>
  );
};

export default DashboardAdmin;
