import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { fetchProtectedData, getUsers,  updateUser, deleteUser } from "../services/api";

const DashboardAdmin = () => {
  const { logout, token } = useContext(AuthContext);

  const [protectedData, setProtectedData] = useState(null);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [editMode, setEditMode] = useState(null);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    const loadData = async () => {
      try {
        const data = await fetchProtectedData(token);
        setProtectedData(data);
        const usersList = await getUsers(token);
        setUsers(usersList);
      } catch (err) {
        setError("Error al cargar datos o usuarios: " + err.message);
      }
    };

    if (token) loadData();
  }, [token]);

  const handleEdit = (user) => {
    setEditMode(user.usuario);
    setFormData({
      rango: user._userInfo?.rango || "",
      creditos: user._userInfo?.creditos || 0,
    });
  };

  const handleCancel = () => {
    setEditMode(null);
    setFormData({});
  };

  const handleSave = async (usuario) => {
    try {
      const newData = {
        _userInfo: {
          rango: formData.rango,
          creditos: Number(formData.creditos),
        },
      };

      const updatedUser = await updateUser(usuario, newData, token);

      setUsers((prevUsers) =>
        prevUsers.map((u) => (u.usuario === usuario ? updatedUser.user : u))
      );

      setEditMode(null);
      setFormData({});
    } catch (err) {
      setError("Error actualizando usuario: " + err.message);
    }
  };

  const handleDelete = async (usuario) => {
    try {
      await deleteUser(usuario, token);
      setUsers((prevUsers) => prevUsers.filter((u) => u.usuario !== usuario));
    } catch (err) {
      setError("Error eliminando usuario: " + err.message);
    }
  };

  if (error) return <p className="text-red-500 p-4">{error}</p>;
  if (!protectedData || users.length === 0) return <p className="p-4">Cargando...</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center">Panel de Administración</h1>

      <section className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Usuarios</h2>
        <div className="grid gap-4">
          {users.map((user) => (
            <div
              key={user.usuario || user.id}
              className="bg-white shadow-md rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between"
            >
              {editMode === user.usuario ? (
                <div className="flex flex-col md:flex-row gap-2 w-full md:items-center">
                  <input
                    type="text"
                    value={formData.rango}
                    onChange={(e) => setFormData({ ...formData, rango: e.target.value })}
                    placeholder="Rango"
                    className="border px-2 py-1 rounded w-full md:w-32"
                  />
                  <input
                    type="number"
                    value={formData.creditos}
                    onChange={(e) =>
                      setFormData({ ...formData, creditos: e.target.value })
                    }
                    placeholder="Créditos"
                    className="border px-2 py-1 rounded w-full md:w-24"
                  />
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleSave(user.usuario)}
                      className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
                    >
                      Guardar
                    </button>
                    <button
                      onClick={handleCancel}
                      className="bg-gray-400 text-white px-3 py-1 rounded hover:bg-gray-500"
                    >
                      Cancelar
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex flex-col md:flex-row md:items-center md:justify-between w-full">
                  <div>
                    <p className="font-semibold">{user.usuario}</p>
                    <p className="text-sm text-gray-600">
                      Rango: {user._userInfo?.rango || "Sin rango"} | Créditos:{" "}
                      {user._userInfo?.creditos ?? 0}
                    </p>
                  </div>
                  <div className="flex gap-2 mt-2 md:mt-0">
                    <button
                      onClick={() => handleEdit(user)}
                      className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                    >
                      Editar
                    </button>
                    <button
                      onClick={() => handleDelete(user.usuario)}
                      className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                    >
                      Eliminar
                    </button>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      </section>

      <div className="text-center mt-8">
        <button
          onClick={logout}
          className="bg-black text-white px-5 py-2 rounded hover:bg-gray-800"
        >
          Cerrar sesión
        </button>
      </div>
    </div>
  );
};

export default DashboardAdmin;
