import React, { useState } from 'react';

export default function RecuperarContrasena() {
  const [usuario, setUsuario] = useState('');
  const [otp, setOtp] = useState('');
  const [nuevaPassword, setNuevaPassword] = useState('');
  const [mensaje, setMensaje] = useState('');

  const solicitarOtp = async () => {
    try {
      const res = await fetch('http://localhost:4000/api/request-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ usuario }),
      });
      const data = await res.json();
      setMensaje(data.mensaje || 'Error al solicitar OTP');
    } catch {
      setMensaje('Error de conexión al solicitar OTP');
    }
  };

  const verificarOtp = async () => {
    try {
      const res = await fetch('http://localhost:4000/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ usuario, otp, nuevaPassword }),
      });
      const data = await res.json();
      setMensaje(data.mensaje || 'Error al verificar OTP');
      if (data.valido) {
        setUsuario('');
        setOtp('');
        setNuevaPassword('');
      }
    } catch {
      setMensaje('Error de conexión al verificar OTP');
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto' }}>
      <h2>Recuperar contraseña</h2>

      <input
        type="text"
        placeholder="Usuario"
        value={usuario}
        onChange={e => setUsuario(e.target.value)}
        style={{ width: '100%', marginBottom: 8 }}
      />
      <button onClick={solicitarOtp} disabled={!usuario}>
        Solicitar OTP
      </button>

      <hr />

      <input
        type="text"
        placeholder="OTP recibido"
        value={otp}
        onChange={e => setOtp(e.target.value)}
        style={{ width: '100%', marginBottom: 8 }}
      />
      <input
        type="password"
        placeholder="Nueva contraseña"
        value={nuevaPassword}
        onChange={e => setNuevaPassword(e.target.value)}
        style={{ width: '100%', marginBottom: 8 }}
      />
      <button onClick={verificarOtp} disabled={!otp || !nuevaPassword}>
        Verificar OTP y cambiar contraseña
      </button>

      <p>{mensaje}</p>
    </div>
  );
}
