import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { fetchProtectedData, getUsers, updateUser, deleteUser } from "../services/api";
import 'bootstrap/dist/css/bootstrap.min.css';

const DashboardAdmin = () => {
  const { logout, token } = useContext(AuthContext);

  const [protectedData, setProtectedData] = useState(null);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const loadData = async () => {
      try {
        const data = await fetchProtectedData(token);
        setProtectedData(data);

        const usersList = await getUsers(token);
        setUsers(usersList);
      } catch (err) {
        setError("Error al cargar datos o usuarios: " + err.message);
      }
    };

    if (token) loadData();
  }, [token]);

  const handleDelete = async (usuario) => {
    try {
      await deleteUser(usuario, token);
      setUsers(users.filter(u => u.usuario !== usuario));
    } catch (err) {
      alert("Error al eliminar usuario: " + err.message);
    }
  };

  const handleEdit = (usuario) => {
    alert("Aquí puedes abrir un modal o redirigir para editar: " + usuario);
  };

  if (error) return <p className="text-danger">{error}</p>;
  if (!protectedData || users.length === 0) return <p>Cargando...</p>;

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Dashboard Admin</h1>

      <section className="mb-5">
        <h2>Datos generales</h2>
        <p><strong>Mensaje:</strong> {protectedData.message}</p>
      </section>

      <section>
        <h2>Usuarios</h2>
        <ul className="list-group">
          {users.map((user) => (
            <li key={user.id} className="list-group-item d-flex justify-content-between align-items-center">
              <div>
                <h5 className="mb-1">{user.usuario}</h5>
                <small className="text-muted">Correo: {user.correo || "no@correo.com"}</small><br />
                <span className="badge bg-secondary me-2">Rango: {user._userInfo?.rango || "N/A"}</span>
                <span className="badge bg-info text-dark">Créditos: {user._userInfo?.creditos ?? 0}</span>
              </div>
              <div>
                <button className="btn btn-outline-primary btn-sm me-2" onClick={() => handleEdit(user.usuario)}>Editar</button>
                <button className="btn btn-outline-danger btn-sm" onClick={() => handleDelete(user.usuario)}>Eliminar</button>
              </div>
            </li>
          ))}
        </ul>
      </section>

      <div className="mt-4">
        <button className="btn btn-secondary" onClick={logout}>Cerrar sesión</button>
      </div>
    </div>
  );
};

export default DashboardAdmin;
