import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { getUserById } from '../services/api';

const Dashboard = () => {
  const { logout, token, user } = useContext(AuthContext); // user debe tener el UUID o ID
  const [data, setData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const loadData = async () => {
      try {
        // Asume que el backend guarda el ID numérico como user.id
        const response = await getUserById(user.id, token);
        setData(response);
      } catch (err) {
        setError('No autorizado o error al cargar datos');
      }
    };

    if (token && user?.id) loadData();
  }, [token, user]);

  if (error) return <p>{error}</p>;
  if (!data) return <p>Cargando...</p>;

  return (
    <div>
      <h1>Bienvenido al Dashboard</h1>
      <p>Usuario: {data.usuario}</p>
      <p>ID: {data.id}</p>
      <p>Rango: {data.rango}</p>
      <button onClick={logout}>Cerrar sesión</button>
    </div>
  );
};

export default Dashboard;
