import React, { useState } from 'react';
import { requestOtp, verifyOtp } from '../services/api'; // suponiendo que están en services/api.js

export default function RecuperarContrasena() {
  const [usuario, setUsuario] = useState('');
  const [otp, setOtp] = useState('');
  const [nuevaPassword, setNuevaPassword] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [paso, setPaso] = useState(1); // 1 = solicitar OTP, 2 = validar OTP, 3 = cambiar contraseña

  // Solicitar OTP (solo usuario)
  const manejarSolicitudOtp = async () => {
    try {
      const res = await requestOtp(usuario);
      setMensaje(res.mensaje || 'OTP enviado');
      setPaso(2);
    } catch (e) {
      setMensaje('Error al solicitar OTP');
    }
  };

  // Validar OTP
  const manejarValidarOtp = async () => {
    try {
      // Solo validamos OTP aquí, sin cambiar la contraseña aún
      const res = await verifyOtp({ usuario, otp });
      if (res.valido) {
        setMensaje('OTP validado, ahora ingresa tu nueva contraseña');
        setPaso(3);
      } else {
        setMensaje('OTP inválido');
      }
    } catch (e) {
      setMensaje('Error al validar OTP');
    }
  };

  // Cambiar contraseña (ya con OTP validado)
  const manejarCambiarPassword = async () => {
    try {
      const res = await verifyOtp({ usuario, otp, nuevaPassword }); // Aquí tu backend debería distinguir entre validación y cambio
      if (res.valido) {
        setMensaje('Contraseña cambiada con éxito');
        setPaso(1);
        setUsuario('');
        setOtp('');
        setNuevaPassword('');
      } else {
        setMensaje('Error al cambiar contraseña');
      }
    } catch (e) {
      setMensaje('Error al cambiar contraseña');
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto' }}>
      <h2>Recuperar contraseña</h2>

      {paso === 1 && (
        <>
          <input
            type="text"
            placeholder="Usuario"
            value={usuario}
            onChange={e => setUsuario(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <button onClick={manejarSolicitudOtp} disabled={!usuario}>
            Solicitar OTP
          </button>
        </>
      )}

      {paso === 2 && (
        <>
          <input
            type="text"
            placeholder="OTP recibido"
            value={otp}
            onChange={e => setOtp(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <button onClick={manejarValidarOtp} disabled={!otp} style={{ marginRight: '10px' }}>
  Validar OTP
</button>
<button onClick={manejarSolicitudOtp} disabled={!usuario}>
  Volver a solicitar OTPs
</button>
        </>
        
      )}

      {paso === 3 && (
        <>
          <input
            type="password"
            placeholder="Nueva contraseña"
            value={nuevaPassword}
            onChange={e => setNuevaPassword(e.target.value)}
            style={{ width: '100%', marginBottom: 8 }}
          />
          <button onClick={manejarCambiarPassword} disabled={!nuevaPassword}>
            Cambiar contraseña
          </button>
        </>
      )}

      <p>{mensaje}</p>
    </div>
  );
}
